class Solution:
    def maxValue(self, n: str, x: int) -> str:
        if n[0] == '-':
            if x < int(n[1]):
                return f"-{x}{n[1:]}"
            for i in range(1, len(n) - 1):
                if int(n[i + 1]) > x >= int(n[i]):
                    return f"{n[:i + 1]}{x}{n[i + 1:]}"
            return f"{n}{x}"
        else:
            if x > int(n[0]):
                return f"{x}{n}"
            for i in range(len(n) - 1):
                if int(n[i + 1]) < x <= int(n[i]):
                    return f"{n[:i + 1]}{x}{n[i + 1:]}"
            return f"{n}{x}"


if __name__ == '__main__':
    print(Solution().maxValue("", 8))